import React, { useState } from "react";

function CreateArea() {
    const [notes, setNotes] = useState({
        title:"",
        content:""
    });

    function handleChange(event){
        const {name, value} = event.target;
        setNotes((prevNote)=>{
            return {
                ...prevNote,
                [name]: value
            };
        });
    }

    return (
        <div>
            <form>
                <input 
                    name="title" 
                    onChange={handleChange}
                    value={notes.title}
                    placeholder="Title" />
                <textarea 
                    name="content" 
                    onChange={handleChange}
                    value={notes.content}
                    placeholder="Take a note..." rows="3" />
                <button>Add</button>
            </form>
        </div>
    );
}

export default CreateArea;
